+            <br>
+            <p>Atenciosamente,</p>
+            <p><strong>Equipe do Organizador de Boletos</strong></p>
+        </div>
+    `;
+
+    const mailOptions = {
+        from: `"Organizador de Boletos" <${process.env.EMAIL_FROM}>`,
+        to: toEmail,
+        subject: `Aviso de Boletos Vencidos - ${companyName}`,
+        html: emailHtml,
+    };
+
+    try {
+        await transporter.sendMail(mailOptions);
+        console.log(`E-mail de teste enviado com sucesso para ${toEmail}`);
+    } catch (error) {
+        console.error(`Erro ao enviar e-mail de teste para ${toEmail}:`, error);
+        // Lan√ßar o erro para que a rota que chamou saiba que falhou
+        throw error;
+    }
+}
+
+// NOVA FUN√á√ÉO para enviar o e-mail do formul√°rio de contato
+async function sendContactFormEmail(fromName, fromEmail, subject, message) {
+    // O e-mail ser√° enviado PARA o endere√ßo que definirmos no .env
+    const toEmail = process.env.CONTACT_FORM_RECIPIENT;
+
+    // Monta o conte√∫do do e-mail
+    const emailHtml = `
+        <div style="font-family: Arial, sans-serif; line-height: 1.6;">
+            <h2>Nova Mensagem de Contato do Site</h2>
+            <p>Voc√™ recebeu uma nova mensagem atrav√©s do formul√°rio de contato do site LobosTI.</p>
+            <hr>
+            <p><strong>Nome:</strong> ${fromName}</p>
+            <p><strong>E-mail do Remetente:</strong> ${fromEmail}</p>
+            <p><strong>Assunto:</strong> ${subject}</p>
