+            <p style="padding: 10px; border-left: 3px solid #ccc;">${message.replace(/\n/g, '<br>')}</p>
+            <hr>
+        </div>
+    `;
+
+    const mailOptions = {
+        from: `"Site LobosTI" <${process.env.EMAIL_FROM}>`,
+        to: toEmail,
+        subject: `Nova Mensagem de Contato: ${subject}`,
+        html: emailHtml,
+        replyTo: fromEmail
+    };
+
+    try {
+        await transporter.sendMail(mailOptions);
+        console.log(`E-mail de contato de ${fromName} enviado com sucesso para ${toEmail}`);
