+document.addEventListener("DOMContentLoaded", function() {
+    // 1. Encontra os locais na página onde os componentes serão inseridos.
+    const headerPlaceholder = document.getElementById("header-placeholder");
+    const footerPlaceholder = document.getElementById("footer-placeholder");
+    const modalsPlaceholder = document.getElementById("modals-placeholder");
+
+    // 2. Deteta de forma inteligente se estamos numa subpágina para ajustar os caminhos.
