+                }
+                return res.text();
+            })
+            .then(data => {
+                if (placeholder) {
+                    placeholder.innerHTML = data;
+                }
             });
-        });
-    </script>
+    };
     
+    // 3. Carrega todos os componentes HTML ao mesmo tempo para maior eficiência.
+    Promise.all([
+        loadComponent(`${basePath}components/header.html`, headerPlaceholder),
+        loadComponent(`${basePath}components/footer.html`, footerPlaceholder),
+        loadComponent(`${basePath}components/modals.html`, modalsPlaceholder)
+    ]).then(() => {
+        // 4. APENAS DEPOIS de todo o HTML ser carregado, adiciona os scripts de interatividade.
+        // Isto garante que os botões e outros elementos já existem na página quando o JS for executado.
+
+        // Carrega o script que controla os modais (Termos, Privacidade, etc.)
+        const modalScript = document.createElement('script');
+        modalScript.src = `${basePath}scripts/modals.js`;
+        document.body.appendChild(modalScript);
+
+        // Carrega o script que controla o cabeçalho (menu mobile)
+        const headerScript = document.createElement('script');
+        headerScript.src = `${basePath}scripts/header.js`;
+        document.body.appendChild(headerScript);
+        
+    }).catch(error => console.error("Erro crítico ao carregar componentes:", error));
+});
+</script>
+    
     <div id="modals-placeholder"></div>
 
 </body>
