+/* Estilos para o botão de rádio personalizado */
+.radio-custom {
+  display: inline-block;
+  width: 1.5rem; /* 24px */
+  height: 1.5rem; /* 24px */
+  border: 2px solid #4b5563; /* Cor da borda (cinza) */
+  border-radius: 50%;
+  position: relative;
+  vertical-align: middle;
+  cursor: pointer;
+  transition: border-color 0.2s ease-in-out;
+}
+
+/* Círculo interior que aparece quando selecionado */
+.radio-custom::after {
+  content: '';
+  display: block;
+  width: 0.875rem; /* 14px */
+  height: 0.875rem; /* 14px */
+  background-color: #6366f1; /* Cor do preenchimento (índigo) */
+  border-radius: 50%;
+  position: absolute;
+  top: 50%;
+  left: 50%;
+  transform: translate(-50%, -50%) scale(0);
+  transition: transform 0.15s ease-in-out;
+}
+
+/* Mostra o círculo interior quando o rádio escondido está marcado */
+input[type="radio"]:checked + .radio-custom::after {
+  transform: translate(-50%, -50%) scale(1);
+}
+
+/* Muda a cor da borda quando o rádio está marcado para um feedback visual melhor */
+input[type="radio"]:checked + .radio-custom {
+  border-color: #6366f1; /* Cor da borda (índigo) */
 }
