+/* Estilos CSS do Tailwind e animação */
+.fade-in {
+    animation: fadeIn 0.3s ease-in-out;
+}
+
+@keyframes fadeIn {
+    from {
+        opacity: 0;
+        transform: translateY(10px);
+    }
+
+    to {
+        opacity: 1;
+        transform: translateY(0);
+    }
+}
+
+/* Estilos para o calendário e cartões de boletos */
+.day-cell {
+    background-color: #374151;
+    /* gray-700 */
+    border-color: #1f2937;
+    /* gray-800 */
+    border-radius: 0.375rem;
+    /* rounded-md */
+    min-height: 8rem;
+    position: relative;
+}
+
+.day-cell.today {
+    border: 2px solid #6366f1;
+    /* indigo-500 */
+}
+
+.day-cell.has-bills {
+    border: 1px solid #4f46e5;
+    /* indigo-600 */
+}
+
+.day-cell::-webkit-scrollbar {
+    width: 4px;
+}
+
+.day-cell::-webkit-scrollbar-thumb {
+    background-color: #4f46e5;
+    /* indigo-600 */
+    border-radius: 2px;
+}
+
+.day-cell::-webkit-scrollbar-track {
+    background-color: #1f2937;
+    /* gray-800 */
+}
+
+.bill-card {
+    background-color: #4b5563;
+    /* gray-700 */
+    border: 1px solid #374151;
+    /* gray-600 */
+    border-radius: 0.375rem;
+    /* rounded-md */
+    padding: 0.75rem;
+    margin-bottom: 0.75rem;
+}
+
+.bill-card.paid {
+    background-color: #15803d;
+    /* green-700 */
+    border-color: #166534;
+    /* green-800 */
+    color: #d1fae5;
+    /* green-100 */
+}
+
+.bill-card.unpaid {
+    background-color: #4f46e5;
+    /* indigo-700 */
+    border-color: #4338ca;
+    /* indigo-800 */
+    color: #e0e7ff;
+    /* indigo-100 */
+}
+
+/* Ajustes para o input de data para mostrar o ícone */
+input[type="date"]::-webkit-calendar-picker-indicator {
+    filter: invert(1);
+    /* Inverte a cor do ícone para branco */
+}
+
+input[type="date"] {
+    color-scheme: dark;
+    /* Força o tema escuro para o calendário popup */
+}
+
+/* Esconde as setas de TODOS os inputs do tipo 'number' */
+input[type=number]::-webkit-outer-spin-button,
+input[type=number]::-webkit-inner-spin-button {
+    -webkit-appearance: none;
+    margin: 0;
+}
+
+input[type=number] {
+    -moz-appearance: textfield;
+    /* Para o Firefox */
+}
+
+/* Ajusta o tamanho da imagem do código de barras para ser responsivo */
+#barcodeImage {
+    max-width: 100%;
+    height: auto;
+}
